import * as THREE from 'three'
import { OrbitControls } from './jsm/controls/OrbitControls.js'
import { GLTFLoader } from './jsm/loaders/GLTFLoader.js'
import { RGBELoader } from './jsm/loaders/RGBELoader.js'

let camera, scene, renderer

init()
render()

function init () {
  const container = document.createElement('div')
  document.body.appendChild(container)

  camera = new THREE.PerspectiveCamera(45, window.innerWidth / window.innerHeight, 0.25, 20)
  camera.position.set(-1.8, 0.6, 2.7)

  scene = new THREE.Scene()

  // Texture
  const esplanade = require('./textures/royal_esplanade_1k.hdr')

  // Models
  const cells = require('./models/gltf/cells_blender/cell.glb')
  const helmet = require('./models/gltf/helmet/DamagedHelmet.gltf')

  new RGBELoader()
    .setDataType(THREE.UnsignedByteType)
    .load(esplanade, function (texture) {
      const envMap = pmremGenerator.fromEquirectangular(texture).texture

      scene.background = envMap
      scene.environment = envMap

      texture.dispose()
      pmremGenerator.dispose()

      render()

      new GLTFLoader()
        .load(cells, function (gltf) {
          scene.add(gltf.scene)
          render()
        })
    })

  renderer = new THREE.WebGLRenderer({ antialias: true })
  renderer.setPixelRatio(window.devicePixelRatio)
  renderer.setSize(window.innerWidth, window.innerHeight)
  renderer.toneMapping = THREE.ACESFilmicToneMapping
  renderer.toneMappingExposure = 1
  renderer.outputEncoding = THREE.sRGBEncoding
  container.appendChild(renderer.domElement)

  const pmremGenerator = new THREE.PMREMGenerator(renderer)
  pmremGenerator.compileEquirectangularShader()

  const controls = new OrbitControls(camera, renderer.domElement)
  controls.addEventListener('change', render) // use if there is no animation loop
  controls.minDistance = 2
  controls.maxDistance = 10
  controls.target.set(0, 0, -0.2)
  controls.update()

  window.addEventListener('resize', onWindowResize, false)
}

function onWindowResize () {
  camera.aspect = window.innerWidth / window.innerHeight
  camera.updateProjectionMatrix()

  renderer.setSize(window.innerWidth, window.innerHeight)

  render()
}

function render () {
  renderer.render(scene, camera)
}
